import React, { useState } from "react";

function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState(null);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Test backend connection
  const testBackend = async () => {
    try {
      const res = await fetch("http://localhost:5000/ping");
      const json = await res.json();
      alert(`Backend says: ${json.message}`);
    } catch (err) {
      alert("Failed to connect to backend: " + err.message);
    }
  };

  // Ask question to backend
  const askQuestion = async () => {
    if (!question.trim()) return;
    setLoading(true);
    setError(null);
    setAnswer(null);
    setData(null);

    try {
      const response = await fetch("http://localhost:5000/ask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ question }),
      });

      if (!response.ok) {
        const errText = await response.text();
        throw new Error(`Server error: ${errText}`);
      }

      const result = await response.json();

      if (result.error) {
        setError(result.error);
      } else {
        setAnswer(result.answer);
        setData(result.data);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: "auto", padding: 20, fontFamily: "Arial" }}>
      <h1>AI Data Agent</h1>

      <button onClick={testBackend} style={{ marginBottom: 20 }}>
        Test Backend Connection
      </button>

      <textarea
        rows={4}
        placeholder="Type your business question here..."
        value={question}
        onChange={(e) => setQuestion(e.target.value)}
        style={{ width: "100%", padding: 10, fontSize: 16 }}
      />

      <button onClick={askQuestion} disabled={loading} style={{ marginTop: 10, padding: "10px 20px" }}>
        {loading ? "Thinking..." : "Ask"}
      </button>

      {error && <p style={{ color: "red", marginTop: 20 }}>Error: {error}</p>}

      {answer && (
        <>
          <h3>Answer:</h3>
          <p>{answer}</p>
        </>
      )}

      {data && data.length > 0 && (
        <>
          <h3>Data:</h3>
          <table
            style={{
              width: "100%",
              borderCollapse: "collapse",
              marginTop: 10,
              border: "1px solid #ccc",
            }}
          >
            <thead>
              <tr>
                {Object.keys(data[0]).map((col) => (
                  <th
                    key={col}
                    style={{
                      border: "1px solid #ccc",
                      padding: 8,
                      backgroundColor: "#eee",
                      textAlign: "left",
                    }}
                  >
                    {col}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((row, idx) => (
                <tr key={idx}>
                  {Object.values(row).map((val, i) => (
                    <td key={i} style={{ border: "1px solid #ccc", padding: 8 }}>
                      {val?.toString()}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default App;
